<?php/** * Controller is the customized base controller class. * All controller classes for this application should extend from this base class. */class Controller extends CController {    /**     * @var string the default layout for the controller view. Defaults to '//layouts/column1',     * meaning using a single column layout. See 'protected/views/layouts/column1.php'.     */    public $layout = '//layouts/column1';    /**     * @var array context menu items. This property will be assigned to {@link CMenu::items}.     */    public $menu = array();    /**     * @var array the breadcrumbs of the current page. The value of this property will     * be assigned to {@link CBreadcrumbs::links}. Please refer to {@link CBreadcrumbs::links}     * for more details on how to specify this property.     */    public $breadcrumbs = array();    public static $admin = 'admin';    public static $member = 'member';    public static $bank_transaction_type = "eBank";    public static $user_transaction_type = "user";    public static $sales_transaction_type = "sales";    public static $redemtion_transaction_type = "Reward Transfer";    public static $joining_transaction_type = "New Joining";    const RIGHT_ID = 2;    const LEFT_ID = 1;    const PAYMENT_TYPE_ID = 1;    const PAYMENT_TYPE_ID_OTHER = 2;    const PAYMENT_TYPE_ID_PAYSA = 3;    const PLAN_ONE = 1;    const PLAN_TWO = 2;    const PLAN_THREE = 3;    public function actionError() {        if ($error = Yii::app()->errorHandler->error) {            if (Yii::app()->request->isAjaxRequest) {                echo $error['message'];            }            else                $this->render('error', $error);        }    }    public function beforeAction($action) {        if (!Yii::app()->user->isGuest) {            if (Users::isUserLock(Yii::app()->user->getId())) {                // redirect to upgrade                $this->redirect(array('/upgrade/create'));            }            //   return parent::beforeAction($action);        }        return true;    }}