<?php/* * To change this template, choose Tools | Templates * and open the template in the editor. *//** * Description of XcodeModel * * @author jh */class XcodeModel extends CActiveRecord{    //put your code here            public function beforeSave() {                   if($this->isNewRecord){                $this->created_date = UtilityManager::getSqlCurrentDate();            }                                  if(isset($this->modified_date)){                        $this->modified_date = UtilityManager::getSqlCurrentDate();        }                return  parent::beforeSave();    }}